mapper.select:
SELECT CONFIRM_DOCUMENT_PK, CONFIRM_DOCUMENT_ID, COMPANY_ID, DEPARTMENT_ID, CREATE_SYSTEM, CONFIRM_STATUS, DOCUMENT_TYPE, REQUESTER_ID
FROM JXX_CONFIRM_DOCUMENT_MASTER
AND CONFIRM_DOCUMENT_ID = #{confirmDocumentId}
AND CONFIRM_STATUS = #{confirmStatus}
AND REQUESTER_ID = #{requesterId}

SELECT CDM.CONFIRM_DOCUMENT_PK, CDM.CONFIRM_DOCUMENT_ID, CDM.COMPANY_ID, CDM.DEPARTMENT_ID, CDM.CREATE_SYSTEM, CDM.CONFIRM_STATUS, CDM.DOCUMENT_TYPE, CDM.REQUESTER_ID, CDM.CREATE_TIME, CDM.CONFIRM_DOCUMENT_CONTENT_PK, ALM.APPROVAL_LINE_PK, ALM.APPROVAL_LINE_ID, ALM.APPROVAL_STATUS, ALM.APPROVAL_TIME FROM JXX_CONFIRM_DOCUMENT_MASTER CDM INNER JOIN JXX_APPROVAL_LINE_MASTER ALM ON ALM.CONFIRM_DOCUMENT_ID = CDM.CONFIRM_DOCUMENT_ID
AND ALM.APPROVAL_LINE_ID = #{approvalId}
AND ALM.APPROVAL_STATUS = #{approveStatus}
ORDER BY CDM.CREATE_TIME DESC
